CREATE FUNCTION public.fn_is_product_discounted(p_product_id uuid) RETURNS boolean
    LANGUAGE plpgsql
    AS $$
declare v_discount_id uuid;
begin
	select d.id
	into v_discount_id
	from discounts d
	join product_variants_discounts pvd on pvd.discount_id = d.id
	join product_variants pv on pv.id = pvd.product_variant_id 
	join products p on p.id = pv.product_id
	where p.id = p_product_id;

	if v_discount_id is not null then
		return true;
	else
		return false;
	end if;
end;
$$;

