CREATE VIEW public.product_ratings_view AS
 SELECT p.id AS product_id,
    COALESCE(avg(pr.rating), (0)::numeric) AS rating,
    COALESCE(count(pr.rating), (0)::bigint) AS reviews_count
   FROM ((public.products p
     JOIN public.product_variants pv ON ((pv.product_id = p.id)))
     LEFT JOIN public.product_reviews pr ON ((pv.id = pr.product_variant_id)))
  GROUP BY p.id;

