    SEQUENCE NAME public.product_variants_discounts_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);

ALTER TABLE public.product_variants ALTER COLUMN id ADD GENERATED ALWAYS AS IDENTITY (
    SEQUENCE NAME public.product_variants_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE
    CACHE 1
);

CREATE VIEW public.product_variants_slim_view AS
 SELECT pv.id,
    pvr.rating,
    pvr.reviews_count,
    (pv.price_offset + p.base_price) AS price,
    d.type AS discount_type,
    d.value AS discount_value
   FROM ((((public.product_variants pv
     JOIN public.products p ON ((p.id = pv.product_id)))
     JOIN public.product_variant_ratings_view pvr ON ((pvr.product_variant_id = pv.id)))
     JOIN public.product_variants_discounts pvd ON ((pvd.product_variant_id = pv.id)))
     JOIN public.discounts d ON ((d.id = pvd.discount_id)));

CREATE VIEW public.products_slim_view AS
 SELECT p.id,
    p.name,
    pb.name AS brand,
    c.name AS category,
    public.fn_get_product_product_variants_ids(p.id) AS variant_ids,
    public.fn_get_all_product_attributes(p.id) AS attributes,
    prv.rating,
    prv.reviews_count
   FROM ((((public.products p
     JOIN public.product_brands pb ON ((pb.id = p.brand_id)))
     JOIN public.product_categories pc ON ((pc.id = p.category_id)))
     JOIN public.categories c ON ((c.id = pc.category_id)))
     JOIN public.product_ratings_view prv ON ((prv.product_id = p.id)));

